import React from 'react';
import { Row, Col } from 'antd';
import 'antd/dist/antd.css'
import './GameList.css';
import iconlv from '../../images/GameList/1@2x.png';
import right from '../../images/GameList/Right@32.png'

class GameList extends React.Component{
    constructor(porps){
        super(porps);
        this.state={
            list:[
                {   
                    id:1,
                    img:"https://lh3.googleusercontent.com/WRTLHUBSdHGJkRRWhrnt_XIuoOPX7RFRwnO9U56l-OXVrFGfKQpEUyyuCEabadj3NX8=s180-rw",
                    name:'Game Name',
                    desc:"这是简介这是简介这是简介这是简介这是简介这是简介这是简，介这是简介这是简介，这是简介这是简介这是简介这是简介这是简介，这是简介这是简介。",
                    lv:3,
                },
                {
                    id:2,
                    img:"https://lh3.googleusercontent.com/WRTLHUBSdHGJkRRWhrnt_XIuoOPX7RFRwnO9U56l-OXVrFGfKQpEUyyuCEabadj3NX8=s180-rw",
                    name:'Game Name',
                    desc:"这是简介这是简介这是简介这是简介这是简介这是简介这是简，介这是简介这是简介，这是简介这是简介这是简介这是简介这是简介，这是简介这是简介。",
                    lv:3,
                },
                {
                    id:3,
                    img:"https://lh3.googleusercontent.com/WRTLHUBSdHGJkRRWhrnt_XIuoOPX7RFRwnO9U56l-OXVrFGfKQpEUyyuCEabadj3NX8=s180-rw",
                    name:'Game Name',
                    desc:"这是简介这是简介这是简介这是简介这是简介这是简介这是简，介这是简介这是简介，这是简介这是简介这是简介这是简介这是简介，这是简介这是简介。",
                    lv:3,
                },
                {
                    id:4,
                    img:"https://lh3.googleusercontent.com/WRTLHUBSdHGJkRRWhrnt_XIuoOPX7RFRwnO9U56l-OXVrFGfKQpEUyyuCEabadj3NX8=s180-rw",
                    name:'Game Name',
                    desc:"这是简介这是简介这是简介这是简介这是简介这是简介这是简，介这是简介这是简介，这是简介这是简介这是简介这是简介这是简介，这是简介这是简介。",
                    lv:3,
                },
                {
                    id:5,
                    img:"https://lh3.googleusercontent.com/WRTLHUBSdHGJkRRWhrnt_XIuoOPX7RFRwnO9U56l-OXVrFGfKQpEUyyuCEabadj3NX8=s180-rw",
                    name:'Game Name',
                    desc:"这是简介这是简介这是简介这是简介这是简介这是简介这是简，介这是简介这是简介，这是简介这是简介这是简介这是简介这是简介，这是简介这是简介。",
                    lv:3,
                },
                {
                    id:6,
                    img:"https://lh3.googleusercontent.com/WRTLHUBSdHGJkRRWhrnt_XIuoOPX7RFRwnO9U56l-OXVrFGfKQpEUyyuCEabadj3NX8=s180-rw",
                    name:'Game Name',
                    desc:"这是简介这是简介这是简介这是简介这是简介这是简介这是简，介这是简介这是简介，这是简介这是简介这是简介这是简介这是简介，这是简介这是简介。",
                    lv:3,
                }
            ]
        }
    }

    lvShow=()=>{
        return (
            <div>
            <img src={iconlv} alt=""/>
            <img src={iconlv} alt=""/>
            <img src={iconlv} alt=""/>
            </div>
        );

    }
    renderOper = (value, index, record) => {
        return (
          <span>
            {/* 用户详情 */}
            <TabDialog size="small" buttonShape="warning" index={index} record={record} showDialog={this.showDialog}/>&nbsp;&nbsp;
            {/* 编辑 */}
            <SimpleFormDialog size="small" buttonShape="warning" index={index} record={record} showDialog={this.showDialog}/>
          </span>
        )
      }

    

    render(){
        return(
            <div id="GameLiet">
                <div className="head">
                    <div className="new-game">New Games</div>
                    <div className="more">More</div>
                </div>
                <div>
                    {
                        this.state.list.map(function(item){
                            return (
                                <Row  className="game-list" key={item.id}>
                                    <Col span="5" className="game-img">
                                        <img src={item.img} alt=""/>
                                    </Col>
                                    <Col span="14" className="game-text">
                                        <div className="game-name">{item.name}</div>
                                        <div className="game-lv" cell={()=>this.renderOper}>
                                            <img src={iconlv} alt=""/>
                                        </div>
                                        <div className="introduction">{item.desc}</div>
                                    </Col>
                                    <Col  span="3" className="more-ico">
                                        <img src={right} alt=""/>
                                    </Col>
                                </Row>
                            )
                        })
                    }
                    
                </div>
            </div>
        )
    }
}

export default GameList;
