import React from 'react';
import { Row, Col,Spin, Alert } from 'antd';
import 'antd/dist/antd.css'
import './GameList.css';
import axios from 'axios';
import iconlv from '../../images/GameList/1@2x.png';
import right from '../../images/GameList/Right@32.png';

var list=[
    {   
        id:1,
        img:"http://pic14.photophoto.cn/20100312/0005018312012169_b.jpg",
        name:'Game Name',
        desc:"这是简介这是简介这是简介这是简介这是简，介这是简介这是简介，这是简介这是简介这是简介这是简介这是简介，这是简介这是简介。",
        lv:1,
    },
    {
        id:2,
        img:"http://img.zcool.cn/community/01ca655600b1866ac7251df8918ba4.png",
        name:'Game Name',
        desc:"这是简介这是简介这是简介这是简介这是简，介这是简介这是简介，这是简介这是简介这是简介这是简介这是简介，这是简介这是简介。",
        lv:3,
    },
    {
        id:3,
        img:"http://pic51.nipic.com/file/20141031/8098773_204453536000_2.jpg",
        name:'Game Name',
        desc:"这是简介这是简介这是简介这是简介这是简，介这是简介这是简介，这是简介这是简介这是简介这是简介这是简介，这是简介这是简介。",
        lv:2,
    },
    {
        id:4,
        img:"http://pic.58pic.com/58pic/11/95/40/07S58PICXpC.jpg",
        name:'Game Name',
        desc:"这是简介这是简介这是简介这是简介这是简，介这是简介这是简介，这是简介这是简介这是简介这是简介这是简介，这是简介这是简介。",
        lv:5,
    },
    {
        id:5,
        img:"http://www.eise.org.cn/upload/img/8oSJh4ROwYGw3hfPpivZJJDiMcsRsOwF8iUqGHIAUwxCRJawempoMRSNJYXM7y77LKSUFVGVkclgTQcxmg.jpg",
        name:'Game Name',
        desc:"这是简介这是简介这是简介这是简介这是简，介这是简介这是简介，这是简介这是简介这是简介这是简介这是简介，这是简介这是简介。",
        lv:3,
    },
    {
        id:6,
        img:"http://pic26.photophoto.cn/20130110/0005018304499726_b.jpg",
        name:'Game Name',
        desc:"这是简介这是简介这是简介这是简介这是简，介这是简介这是简介，这是简介这是简介这是简介这是简介这是简介，这是简介这是简介。",
        lv:4,
    }
]

const screenHeight=window.screen.availHeight //当前屏幕高度
const screenWidth=window.screen.availWidth //当前屏幕宽度

class GameList extends React.Component{
    constructor(porps,context){
        super(porps,context);
        //设置当前页面title
        document.getElementsByTagName("title")[0].innerText = 'Game Center';
        this.state={
            list:[],
            pageSize:6,
            pageNum:1,//当前页码
            isend:false,//是否可滚动
            loading:true
        }
        this.getGameList()
    }

    onLoadPage(){
        let clientHeight = document.documentElement.clientHeight;
        let scrollHeight = document.body.scrollHeight;
        let scrollTop = document.documentElement.scrollTop;

        let proLoadDis = 30;//预值

        if((scrollTop + clientHeight) >= scrollHeight - proLoadDis){
            this.setState({
                pageNum:++this.state.pageNum,
            })
            if(this.state.pageNum*this.state.pageSize<=this.state.total+this.state.pageSize){
                this.getGameList()
            }else{
                this.setState({
                    loading:false
                })
            }
        }
    }

    componentWillMount(){
        window.addEventListener('scroll',()=>this.onLoadPage())
    }
    componentWillUnmount(){
        window.removeEventListener('scroll',()=>this.onLoadPage())
    }

    // 获取游戏列表
    getGameList(){
        axios.get('http://192.168.1.199/mfun/game/list?status='+1+'&pageSize='+this.state.pageSize+'&pageNum='+this.state.pageNum
        ).then((res)=>{
            console.log(res.data.code)
            this.setState({
                list:this.state.list.concat(res.data.data.data),
                total:res.data.data.total
            })
            list=res.data.data
        })
    }

    render(){
        return(
            <div id="GameLiet" style={{height:screenHeight+'px'}}>
                <div className="head">
                    <div className="new-game">New Games</div>
                    <div className="more">More</div>
                </div>
                <div className="list">
                    {
                        this.state.list.map(function(item){
                            return (
                                <Row  className="game-list" key={item.id}>
                                    <Col span="5" className="game-img">
                                        {/* <a href="https://www.jianshu.com/p/d1ef832e8e65"> */}
                                            <img src={item.icon} alt=""/>
                                        {/* </a> */}
                                    </Col>
                                    <Col span="14" className="game-text">
                                        <div className="game-name">{item.name}</div>
                                        <div className="game-lv">
                                            {item.lv>=1?<img src={iconlv} alt=""/>:''}
                                            {item.lv>=2?<img src={iconlv} alt=""/>:''}
                                            {item.lv>=3?<img src={iconlv} alt=""/>:''}
                                            {item.lv>=4?<img src={iconlv} alt=""/>:''}
                                            {item.lv>=5?<img src={iconlv} alt=""/>:''}
                                        </div>
                                        <div className="introduction">{item.desc}</div>
                                    </Col>
                                    <Col  span="3" className="more-ico">
                                        <img src={right} alt=""/>
                                    </Col>
                                </Row>
                            )
                        })
                    }
                </div>
                <div className="loading">
                    {
                        this.state.loading?<Spin tip="Loading..."></Spin>:'到底了！'
                    }
                    
                </div>
            </div>
        )
    }
}

export default GameList;
